mapper.update:
update

set RESULT = #{RESULT},
	MESSAGE = #{MESSAGE}
where REPORT_ID = #{REPORT_ID}

mapper.select:
select CONCAT(RUN_PARAM,',',P1,',',P2,',',P3,',',P4,',',P5) REPORT_PARAM, OPERATOR, RUN_DATETIME, OUTBOND_PATH, RESULT, MESSAGE, REPORT_ID from
where 1=1
and (
RUN_PARAM LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or OUTBOND_PATH LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and RUN_DATETIME >= #{pd.lastStart}
and RUN_DATETIME <= #{pd.lastEnd}
and RESULT = #{pd.RESULT}
order by RUN_DATETIME desc

select sf.FUND_CODE, sf.FUND_ID, sf.FIRM_CODE, sf.SHORT_NAME, sf.FULL_NAME, sf.FULL_NAME_ORIGINAL, sf.TA_NAME, sf.MF, sf.ETF, sf.STRUCTURED, sf.DATE_FROM, sf.DATE_TO, sf.DATE_TRANSFORM from sys_fund_info sf where sf.FIRM_CODE = #{FIRM_CODE}
and sf.FUND_ID = #{FUND_ID}
and sf.MF = #{MF}
and sf.ETF = #{ETF}
and sf.STRUCTURED = #{STRUCTURED}
