mapper.select:
select

from 

where 1=1

	and
		(

			RUN_PARAM LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
			 or 
			OPERATOR LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') 
		)


    and RUN_DATETIME >= #{pd.lastStart}


    and RUN_DATETIME <= #{pd.lastEnd}


    and RESULT = #{pd.RESULT}

order by RUN_DATETIME desc

SELECT sfi.FUND_ID, sfi.FIRM_CODE, sfi.SHORT_NAME, smc.SHORT_NAME MGR_COMPANY, (SELECT COUNT(*) FROM sys_concrecord c where c.WP_TYPE = 'C' AND c.STATUS='S' AND c.FUND_ID = sfi.FUND_ID AND c.PERIOD = #{PERIOD}) CFLAG, (SELECT COUNT(*) FROM sys_concrecord e where e.WP_TYPE = 'E' AND e.STATUS='S' AND e.FUND_ID = sfi.FUND_ID AND e.PERIOD = #{PERIOD}) EFLAG, (SELECT COUNT(*) FROM sys_concrecord g where g.WP_TYPE = 'G' AND g.STATUS='S' AND g.FUND_ID = sfi.FUND_ID AND g.PERIOD = #{PERIOD}) GFLAG, (SELECT COUNT(*) FROM sys_concrecord h where h.WP_TYPE = 'H' AND h.STATUS='S' AND h.FUND_ID = sfi.FUND_ID AND h.PERIOD = #{PERIOD}) HFLAG, (SELECT COUNT(*) FROM sys_concrecord n where n.WP_TYPE = 'N' AND n.STATUS='S' AND n.FUND_ID = sfi.FUND_ID AND n.PERIOD = #{PERIOD}) NFLAG, (SELECT COUNT(*) FROM sys_concrecord p where p.WP_TYPE = 'P' AND p.STATUS='S' AND p.FUND_ID = sfi.FUND_ID AND p.PERIOD = #{PERIOD}) PFLAG, (SELECT COUNT(*) FROM sys_concrecord t where t.WP_TYPE = 'T' AND t.STATUS='S' AND t.FUND_ID = sfi.FUND_ID AND t.PERIOD = #{PERIOD}) TFLAG, (SELECT COUNT(*) FROM sys_concrecord u where u.WP_TYPE = 'U' AND u.STATUS='S' AND u.FUND_ID = sfi.FUND_ID AND u.PERIOD = #{PERIOD}) UFLAG, (SELECT COUNT(*) FROM sys_concrecord v where v.WP_TYPE = 'V' AND v.STATUS='S' AND v.FUND_ID = sfi.FUND_ID AND v.PERIOD = #{PERIOD}) VFLAG, (SELECT COUNT(*) FROM sys_concrecord i where i.WP_TYPE = 'I' AND i.STATUS='S' AND i.FUND_ID = sfi.FUND_ID AND i.PERIOD = #{PERIOD}) IFLAG, (SELECT COUNT(*) FROM sys_concrecord o where o.WP_TYPE = 'O' AND o.STATUS='S' AND o.FUND_ID = sfi.FUND_ID AND o.PERIOD = #{PERIOD}) OFLAG, (SELECT COUNT(*) FROM sys_concrecord sa where sa.WP_TYPE = 'SA' AND sa.STATUS='S' AND sa.FUND_ID = sfi.FUND_ID AND sa.PERIOD = #{PERIOD}) SAFLAG FROM sys_fund_info sfi LEFT JOIN sys_mgrcompany smc ON sfi.FIRM_CODE = smc.COMPANY_CODE WHERE 1 = 1
and sfi.FIRM_CODE = #{FIRM_CODE}
and sfi.FUND_ID = #{FUND_ID}
