mapper.select:
SELECT 'H400' as source, H400M.PARTY_SHORT_NAME AS partyShortName, H400M.RELATIONSHIP AS relationship, H400M.`CODE` AS `code`, H400M.`NAME` AS `name`, H400M.TOTAL_COST AS totalCost, H400M.MKT_VALUE_CLIENT AS mktValueClient, H400M.APPRECIATION_CLIENT AS appreciationClient,
H400M.TYPE as type, H400M.ACCOUNT_NUM as accountNum, H400M.MARKET as market, H400M.SUB_TYPE as subType, H400M.QUANTITY as quantity, H400M.UNIT_PRICE as unitPrice, H400M.VAL_PRICE as valPrice, H400M.RELEATED as releated, H400M.THREE_LEVEL as threeLevel, H400M.RESTRICT_TYPE as restrictType FROM wp_h400_main H400M where 1 = 1 and H400M.STATUS = 'Y'
AND H400M.RELEATED = #{related}
and H400M.FUND_ID = #{fundId} and H400M.PERIOD = #{period}

SELECT 'H500' as source, H500M.PARTY_SHORT_NAME AS partyShortName, H500M.RELATIONSHIP AS relationship, H500M.BOND_CODE AS `code`, H500M.BOND_NAME AS `name`, H500M.TOTAL_COST AS totalCost, H500M.MKT_VALUE_CLIENT AS mktValueClient, H500M.APPRECIATION_CLIENT AS appreciationClient,
H500M.TYPE AS type, H500M.ACCOUNT_NUM AS accountNum, H500M.MARKET AS market, H500M.SUB_TYPE AS subType, H500M.QUANTITY AS quantity, H500M.UNIT_PRICE AS unitPrice, H500M.INTEREST_AR_CLIENT AS interestArClient, H500M.VAL_PRICE AS valPrice, H500M.UNIT_INTEREST_AT AS unitInterestAt, H500M.RELEATED AS releated, H500M.THREE_LEVEL AS threeLevel, H500M.RESTRICT_TYPE AS restrictType, H500M.EXPOSURE_PERIOD AS exposurePeriod FROM wp_h500_main H500M where 1 = 1 and H500M.STATUS = 'Y'
AND H500M.RELEATED = #{related}
and H500M.FUND_ID = #{fundId} and H500M.PERIOD = #{period}
